package pages;import org.openqa.selenium.Alert;import org.openqa.selenium.WebElement;import org.openqa.selenium.chrome.ChromeDriver;import org.openqa.selenium.support.FindBy;import org.openqa.selenium.support.PageFactory;public class BasePage {	@FindBy(className = "logo")	WebElement logoLocator;	@FindBy(className = "login-icon")	WebElement loginLocator;	@FindBy(className = "search-button")	WebElement searchbuttonLocator;	@FindBy(id = "search-input")	WebElement searchfieldLocator;	@FindBy(className = "search-submit")	WebElement searchconfirmationLocator;	@FindBy(id = "menu-button")	WebElement burrgerButton;	ChromeDriver driver = null;	public BasePage(ChromeDriver driver) {		PageFactory.initElements(driver, this);		this.driver = driver;	}	public BasePage clickBurgerButton() {		burrgerButton.click();		return this;	}	public BasePage clickOnLoginIcon() {		loginLocator.click();		return this;	}	public  BasePage searchItem(String wheyProtein) {		searchfieldLocator.sendKeys();		return this;	}}